const iterations = Math.min(inputs.iterations || 4, 8);
const axiom = parameters.axiom || 'F';
const rule = parameters.rule || 'F=F[+F]F[-F]F';
const angle = (parameters.angle || 25) * Math.PI / 180;
const segmentLength = parameters.length || 1;

// Parse rule
const rules = {};
const ruleParts = rule.split('=');
if (ruleParts.length === 2) {
  rules[ruleParts[0]] = ruleParts[1];
}

// Generate L-system string
let current = axiom;
for (let i = 0; i < iterations; i++) {
  let next = '';
  for (const char of current) {
    next += rules[char] || char;
  }
  current = next;
}

// Interpret string as turtle graphics
const vertices = [];
const stack = [];
let x = 0, y = 0, heading = Math.PI / 2;

for (const char of current) {
  switch (char) {
    case 'F':
      const newX = x + Math.cos(heading) * segmentLength;
      const newY = y + Math.sin(heading) * segmentLength;
      vertices.push(x, 0, y);
      vertices.push(newX, 0, newY);
      x = newX;
      y = newY;
      break;
    case '+':
      heading += angle;
      break;
    case '-':
      heading -= angle;
      break;
    case '[':
      stack.push({ x, y, heading });
      break;
    case ']':
      if (stack.length > 0) {
        const state = stack.pop();
        x = state.x;
        y = state.y;
        heading = state.heading;
      }
      break;
  }
}

return { vertices };

const time = inputs.time || 0;
const gridSize = Math.min(parameters.gridSize || 32, 128);
const viscosity = parameters.viscosity || 0.1;
const waveSpeed = parameters.waveSpeed || 1;
const amplitude = parameters.amplitude || 0.5;

const vertices = [];
const size = 10;

// Simple wave equation simulation
for (let i = 0; i < gridSize; i++) {
  for (let j = 0; j < gridSize; j++) {
    const x = (i / gridSize - 0.5) * size;
    const z = (j / gridSize - 0.5) * size;

    // Multiple wave sources
    const dist1 = Math.sqrt((x - 2) ** 2 + (z - 2) ** 2);
    const dist2 = Math.sqrt((x + 2) ** 2 + (z + 2) ** 2);
    const dist3 = Math.sqrt(x ** 2 + z ** 2);

    // Wave interference pattern
    const wave1 = Math.sin(dist1 * waveSpeed - time * 2) * amplitude;
    const wave2 = Math.sin(dist2 * waveSpeed - time * 1.5) * amplitude * 0.7;
    const wave3 = Math.sin(dist3 * waveSpeed - time * 2.5) * amplitude * 0.5;

    // Apply viscosity damping
    const damping = Math.exp(-viscosity * Math.max(dist1, dist2, dist3));
    const height = (wave1 + wave2 + wave3) * damping;

    // Add some ripple effects
    const ripple = Math.sin(x * 0.5 + time) * Math.cos(z * 0.5 + time) * 0.1 * amplitude;

    vertices.push(x, height + ripple, z);
  }
}

return { vertices };

const height = inputs.height || 10;
const archType = parameters.archType || 'pointed';
const columns = Math.min(parameters.columns || 3, 7);
const detail = parameters.detail || 2;

const vertices = [];

// Gothic arch generation
function createArch(centerX, centerZ, width, height, type) {
  const points = [];
  const segments = 32 * detail;

  for (let i = 0; i <= segments; i++) {
    const t = i / segments;
    let x, y;

    switch (type) {
      case 'pointed':
        if (t <= 0.5) {
          const angle = t * Math.PI;
          x = centerX - width/2 + (width/2) * Math.sin(angle);
          y = height * (1 - Math.cos(angle));
        } else {
          const angle = (1 - t) * Math.PI;
          x = centerX + (width/2) * Math.sin(angle);
          y = height * (1 - Math.cos(angle));
        }
        break;
      case 'lancet':
        const lanceAngle = (t - 0.5) * Math.PI * 1.2;
        x = centerX + (width/2) * Math.sin(lanceAngle);
        y = height * (1 + Math.cos(lanceAngle)) / 2;
        break;
      case 'equilateral':
        const eqAngle = t * Math.PI;
        x = centerX + (width/2) * (t - 0.5) * 2;
        y = height * Math.sin(eqAngle);
        break;
      default: // drop
        const dropAngle = t * Math.PI;
        x = centerX + (width/2) * Math.sin(dropAngle);
        y = height * (Math.sin(dropAngle) + 0.2 * Math.sin(dropAngle * 3));
    }

    points.push([x, y, centerZ]);
  }

  return points;
}

// Generate columns with Gothic styling
for (let col = 0; col < columns; col++) {
  const x = (col - (columns - 1) / 2) * 6;
  const archWidth = 4;

  // Main arch
  const archPoints = createArch(x, 0, archWidth, height, archType);
  for (const point of archPoints) {
    vertices.push(...point);
  }

  // Column shaft with detail
  const columnSegments = 16 * detail;
  for (let i = 0; i <= columnSegments; i++) {
    const y = (i / columnSegments) * height;
    const radius = 0.3 + 0.1 * Math.sin(y * 0.5); // Varying column thickness

    // Add spiral details
    const spiralDetail = detail > 1 ? 0.05 * Math.sin(y * 2 + col) : 0;
    const finalRadius = radius + spiralDetail;

    // Create column cross-section
    for (let j = 0; j < 8; j++) {
      const angle = (j / 8) * Math.PI * 2;
      const colX = x + finalRadius * Math.cos(angle);
      const colZ = finalRadius * Math.sin(angle);
      vertices.push(colX, y, colZ);
    }
  }

  // Capital (column top decoration)
  if (detail >= 2) {
    const capHeight = height * 0.9;
    for (let i = 0; i < 12; i++) {
      const angle = (i / 12) * Math.PI * 2;
      const capRadius = 0.6;
      const leafX = x + capRadius * Math.cos(angle);
      const leafZ = capRadius * Math.sin(angle);

      // Leaf-like capital ornaments
      vertices.push(x, capHeight, 0);
      vertices.push(leafX, capHeight + 0.3, leafZ);
      vertices.push(leafX * 0.8, capHeight + 0.5, leafZ * 0.8);
    }
  }
}

// Flying buttresses
if (detail >= 3 && columns > 1) {
  for (let i = 0; i < columns - 1; i++) {
    const x1 = (i - (columns - 1) / 2) * 6;
    const x2 = (i + 1 - (columns - 1) / 2) * 6;
    const buttressHeight = height * 0.7;

    // Curved buttress
    for (let j = 0; j <= 16; j++) {
      const t = j / 16;
      const x = x1 + (x2 - x1) * t;
      const y = buttressHeight + Math.sin(t * Math.PI) * 2;
      const z = -3 + Math.sin(t * Math.PI) * 1;
      vertices.push(x, y, z);
    }
  }
}

return { vertices };

const time = inputs.time || 0;
const turns = parameters.turns || 3;
const basePairs = Math.min(parameters.basePairs || 30, 100);
const radius = parameters.radius || 1;
const showBases = parameters.showBases !== false;

const vertices = [];
const height = turns * 3.4; // 3.4 units per turn (DNA pitch)
const anglePerPair = (turns * 2 * Math.PI) / basePairs;

// DNA consists of two antiparallel strands
for (let i = 0; i < basePairs; i++) {
  const y = (i / basePairs) * height;
  const angle1 = i * anglePerPair + time;
  const angle2 = angle1 + Math.PI; // 180Â° offset for second strand

  // First strand (sugar-phosphate backbone)
  const x1 = radius * Math.cos(angle1);
  const z1 = radius * Math.sin(angle1);

  // Second strand
  const x2 = radius * Math.cos(angle2);
  const z2 = radius * Math.sin(angle2);

  // Add backbone points
  vertices.push(x1, y, z1);
  vertices.push(x2, y, z2);

  // Connect adjacent backbone points
  if (i > 0) {
    const prevY = ((i-1) / basePairs) * height;
    const prevAngle1 = (i-1) * anglePerPair + time;
    const prevAngle2 = prevAngle1 + Math.PI;

    const prevX1 = radius * Math.cos(prevAngle1);
    const prevZ1 = radius * Math.sin(prevAngle1);
    const prevX2 = radius * Math.cos(prevAngle2);
    const prevZ2 = radius * Math.sin(prevAngle2);

    // Connect current to previous for strand continuity
    vertices.push(prevX1, prevY, prevZ1);
    vertices.push(x1, y, z1);
    vertices.push(prevX2, prevY, prevZ2);
    vertices.push(x2, y, z2);
  }

  // Base pairs (horizontal connections)
  if (showBases) {
    // Major groove positioning
    const baseOffset1 = 0.7; // Distance from backbone to base
    const baseOffset2 = 0.7;

    const baseX1 = x1 + baseOffset1 * Math.cos(angle1 + Math.PI/2);
    const baseZ1 = z1 + baseOffset1 * Math.sin(angle1 + Math.PI/2);
    const baseX2 = x2 + baseOffset2 * Math.cos(angle2 - Math.PI/2);
    const baseZ2 = z2 + baseOffset2 * Math.sin(angle2 - Math.PI/2);

    // Connect bases across the helix
    vertices.push(baseX1, y, baseZ1);
    vertices.push(baseX2, y, baseZ2);

    // Add base representations (small perpendicular segments)
    const baseLength = 0.2;

    // Base on strand 1
    const perpAngle1 = angle1 + Math.PI/2;
    vertices.push(
      baseX1 - baseLength * Math.cos(perpAngle1), y, baseZ1 - baseLength * Math.sin(perpAngle1),
      baseX1 + baseLength * Math.cos(perpAngle1), y, baseZ1 + baseLength * Math.sin(perpAngle1)
    );

    // Base on strand 2
    const perpAngle2 = angle2 + Math.PI/2;
    vertices.push(
      baseX2 - baseLength * Math.cos(perpAngle2), y, baseZ2 - baseLength * Math.sin(perpAngle2),
      baseX2 + baseLength * Math.cos(perpAngle2), y, baseZ2 + baseLength * Math.sin(perpAngle2)
    );
  }

  // Add minor groove details
  if (i % 3 === 0) { // Every third base pair
    const grooveRadius = radius * 0.8;
    const grooveX = grooveRadius * Math.cos(angle1 + Math.PI/4);
    const grooveZ = grooveRadius * Math.sin(angle1 + Math.PI/4);
    vertices.push(grooveX, y, grooveZ);
  }
}

return { vertices };

const seed = inputs.seed || 456;
let width = parameters.width || 15;
let height = parameters.height || 15;
const wallHeight = parameters.wallHeight || 3;
const complexity = parameters.complexity || 0.5;

// Ensure odd dimensions for proper maze generation
width = width % 2 === 0 ? width + 1 : width;
height = height % 2 === 0 ? height + 1 : height;

width = Math.min(width, 31);
height = Math.min(height, 31);

// Seeded random function
let randomSeed = seed;
function seededRandom() {
  randomSeed = (randomSeed * 9301 + 49297) % 233280;
  return randomSeed / 233280;
}

// Initialize maze grid (true = wall, false = path)
const maze = [];
for (let y = 0; y < height; y++) {
  maze[y] = [];
  for (let x = 0; x < width; x++) {
    maze[y][x] = true; // Start with all walls
  }
}

// Recursive backtracking maze generation
function carvePath(x, y) {
  maze[y][x] = false; // Create path

  // Directions: up, right, down, left
  const directions = [
    {dx: 0, dy: -2},
    {dx: 2, dy: 0},
    {dx: 0, dy: 2},
    {dx: -2, dy: 0}
  ];

  // Shuffle directions
  for (let i = directions.length - 1; i > 0; i--) {
    const j = Math.floor(seededRandom() * (i + 1));
    [directions[i], directions[j]] = [directions[j], directions[i]];
  }

  for (const dir of directions) {
    const nx = x + dir.dx;
    const ny = y + dir.dy;

    // Check bounds and if cell is unvisited
    if (nx > 0 && nx < width - 1 && ny > 0 && ny < height - 1 && maze[ny][nx]) {
      // Carve through the wall between current and next cell
      maze[y + dir.dy / 2][x + dir.dx / 2] = false;
      carvePath(nx, ny);
    }
  }
}

// Start maze generation from (1,1)
carvePath(1, 1);

// Add some random complexity
const extraPaths = Math.floor(complexity * width * height * 0.1);
for (let i = 0; i < extraPaths; i++) {
  const x = Math.floor(seededRandom() * width);
  const y = Math.floor(seededRandom() * height);
  if (x > 0 && x < width - 1 && y > 0 && y < height - 1) {
    maze[y][x] = false;
  }
}

// Ensure entrance and exit
maze[1][0] = false; // Entrance
maze[height - 2][width - 1] = false; // Exit

// Generate 3D vertices for walls
const vertices = [];
const cellSize = 2;

for (let y = 0; y < height; y++) {
  for (let x = 0; x < width; x++) {
    if (maze[y][x]) { // This is a wall
      const worldX = (x - width / 2) * cellSize;
      const worldZ = (y - height / 2) * cellSize;

      // Create wall cube vertices
      const positions = [
        // Bottom face
        [worldX - cellSize/2, 0, worldZ - cellSize/2],
        [worldX + cellSize/2, 0, worldZ - cellSize/2],
        [worldX + cellSize/2, 0, worldZ + cellSize/2],
        [worldX - cellSize/2, 0, worldZ + cellSize/2],
        // Top face
        [worldX - cellSize/2, wallHeight, worldZ - cellSize/2],
        [worldX + cellSize/2, wallHeight, worldZ - cellSize/2],
        [worldX + cellSize/2, wallHeight, worldZ + cellSize/2],
        [worldX - cellSize/2, wallHeight, worldZ + cellSize/2]
      ];

      // Add all cube vertices
      for (const pos of positions) {
        vertices.push(...pos);
      }

      // Add edges for wireframe representation
      const edges = [
        [0,1], [1,2], [2,3], [3,0], // Bottom edges
        [4,5], [5,6], [6,7], [7,4], // Top edges
        [0,4], [1,5], [2,6], [3,7]  // Vertical edges
      ];

      for (const edge of edges) {
        vertices.push(...positions[edge[0]], ...positions[edge[1]]);
      }
    }
  }
}

return { vertices };
